<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getCalendarAttrs } from "../ctx.js";
import { createDispatcher } from "../../../internal/events.js";
export let date;
export let month;
export let asChild = false;
const {
  elements: { cell },
  helpers: { isDateDisabled, isDateUnavailable, isDateSelected }
} = getCtx();
const attrs = getCalendarAttrs("date");
const dispatch = createDispatcher();
$:
  builder = $cell(date, month);
$:
  Object.assign(builder, attrs);
$:
  slotProps = {
    builder,
    disabled: $isDateDisabled(date),
    unavailable: $isDateUnavailable(date),
    selected: $isDateSelected(date)
  };
</script>

{#if asChild}
	<slot {...slotProps} />
{:else}
	<div {...builder} use:builder.action {...$$restProps} on:m-click={dispatch}>
		<slot {...slotProps}>
			{date.day}
		</slot>
	</div>
{/if}
